mapper.update:
UPDATE AGREEMENTS
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT a.id AS agreementId, a.status AS agreementStatus, a.amount AS amount, a.due_at AS dueAt, a.terms AS terms, a.created_at AS createdAt, i.id AS itemId, i.type AS itemType, i.title AS itemTitle, i.description AS itemDescription, i.status AS itemStatus, i.file_url AS itemFileUrl, i.file_type AS itemFileType, creditor_user.id AS creditorId, creditor_user.name AS creditorName, creditor_party.confirm_at AS creditorConfirmAt, debtor_user.id AS debtorId, debtor_user.name AS debtorName, debtor_party.confirm_at AS debtorConfirmAt FROM AGREEMENTS a JOIN ITEMS i ON a.item_id = i.id JOIN AGREEMENT_PARTIES creditor_party ON a.id = creditor_party.agreement_id AND creditor_party.role = 'CREDITOR' JOIN USERS creditor_user ON creditor_party.user_id = creditor_user.id JOIN AGREEMENT_PARTIES debtor_party ON a.id = debtor_party.agreement_id AND debtor_party.role = 'DEBTOR' JOIN USERS debtor_user ON debtor_party.user_id = debtor_user.id
AND creditor_user.id = #{userId}
AND debtor_user.id = #{userId}
AND 1 = 0
AND a.id < #{lastId}
ORDER by a.id DESC LIMIT #{sizePlusOne}

SELECT partner_user.id AS id, partner_user.name AS name, partner_user.email AS email, partner_user.phone AS phone, recent_partners.last_agreement_id AS lastAgreementId, recent_partners.last_contact_at AS lastContactAt FROM ( SELECT partner_id, MAX(agreement_id) AS last_agreement_id, MAX(created_at) AS last_contact_at FROM ( -- 내가 채권자일 때 상대방(채무자) 정보 SELECT p_debtor.user_id AS partner_id, a.id AS agreement_id, a.created_at FROM AGREEMENTS a JOIN AGREEMENT_PARTIES p_creditor ON a.id = p_creditor.agreement_id AND p_creditor.role = 'CREDITOR' JOIN AGREEMENT_PARTIES p_debtor ON a.id = p_debtor.agreement_id AND p_debtor.role = 'DEBTOR' WHERE p_creditor.user_id = #{userId}
UNION ALL
-- 내가 채무자일 때 상대방(채권자) 정보 SELECT p_creditor.user_id AS partner_id, a.id AS agreement_id, a.created_at FROM AGREEMENTS a JOIN AGREEMENT_PARTIES p_creditor ON a.id = p_creditor.agreement_id AND p_creditor.role = 'CREDITOR' JOIN AGREEMENT_PARTIES p_debtor ON a.id = p_debtor.agreement_id AND p_debtor.role = 'DEBTOR' WHERE p_debtor.user_id = #{userId} ) all_partners GROUP BY partner_id ) AS recent_partners JOIN USERS partner_user ON recent_partners.partner_id = partner_user.id
AND recent_partners.last_agreement_id < #{lastId}
ORDER BY recent_partners.last_agreement_id DESC LIMIT #{sizePlusOne}

mapper.update:
UPDATE AGREEMENTS
SET status = #{status},
    due_at = #{dueAt}
WHERE id = #{id}

UPDATE AGREEMENTS
SET terms = #{terms}
WHERE id = #{id}

mapper.select:
SELECT a.id, u.name AS debtorName, ap.confirm_at AS startDate, a.due_at AS dueDate, a.return_date AS returnDate, a.status, a.terms, DATEDIFF(a.due_at, ap.confirm_at) + 1 AS rentalDays, IF(a.status = 'COMPLETED' AND a.return_date > a.due_at, 1, 0) AS overdueReturn FROM AGREEMENTS a LEFT JOIN AGREEMENT_PARTIES ap ON a.id = ap.agreement_id AND ap.role = 'DEBTOR' LEFT JOIN USERS u ON ap.user_id = u.id WHERE a.item_id = #{itemId}
AND a.id < #{lastId}
ORDER by a.id DESC LIMIT #{sizePlusOne}

SELECT a.id, a.status, a.amount, a.terms, a.due_at, a.return_date, i.title AS itemTitle, i.type AS itemType, i.file_url AS itemFileUrl, u.name AS partnerName, partner_party.role AS partnerRole, CASE WHEN i.type = 'MONEY' THEN a.amount - IFNULL(t_sum.total_paid, 0) ELSE NULL END AS remainingAmount FROM AGREEMENTS a JOIN ITEMS i ON a.item_id = i.id JOIN AGREEMENT_PARTIES my_party ON a.id = my_party.agreement_id AND my_party.user_id = #{userId} JOIN AGREEMENT_PARTIES partner_party ON a.id = partner_party.agreement_id AND partner_party.user_id != #{userId} JOIN USERS u ON partner_party.user_id = u.id LEFT JOIN (SELECT agreement_id, SUM(amount) AS total_paid FROM TRANSACTIONS WHERE status = 'CONFIRMED' GROUP BY agreement_id) t_sum ON a.id = t_sum.agreement_id
AND my_party.role = #{role}
AND a.id < #{lastId}
AND (i.title LIKE CONCAT('%', #{keyword}, '%') OR u.name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY a.id DESC LIMIT #{sizePlusOne}

SELECT COUNT(*) > 0
FROM AGREEMENTS a
       JOIN AGREEMENT_PARTIES ap ON a.id = ap.agreement_id
WHERE a.id = #{id}
  AND ap.user_id = #{userId}

SELECT * FROM ( SELECT 'AGREEMENT' AS source, a.id, CASE WHEN a.status = 'PENDING' THEN '대여 승인 요청' ELSE a.status END AS status, creditorUser.name AS pendingUser, CASE WHEN i.type = 'OBJECT' THEN i.title ELSE CONCAT(a.amount, '원') END AS description, UNIX_TIMESTAMP(a.created_at) * 1000 AS cursorKey FROM AGREEMENTS a JOIN AGREEMENT_PARTIES debtorParty ON a.id = debtorParty.agreement_id AND debtorParty.role = 'DEBTOR' JOIN AGREEMENT_PARTIES creditorParty ON a.id = creditorParty.agreement_id AND creditorParty.role = 'CREDITOR' JOIN USERS debtorUser ON debtorParty.user_id = debtorUser.id JOIN USERS creditorUser ON creditorParty.user_id = creditorUser.id JOIN ITEMS i ON a.item_id = i.id WHERE a.status = 'PENDING' AND debtorParty.user_id = #{userId}
UNION ALL
SELECT 'TRANSACTION' AS source, t.id, CASE WHEN t.status = 'PENDING' THEN '상환 완료 요청' ELSE t.status END AS status, debtorUser.name AS pendingUser, CONCAT(t.amount, '원') AS description, UNIX_TIMESTAMP(t.created_at) * 1000 AS cursorKey FROM TRANSACTIONS t JOIN AGREEMENTS a ON a.id = t.agreement_id JOIN AGREEMENT_PARTIES debtorParty ON a.id = debtorParty.agreement_id AND debtorParty.role = 'DEBTOR' JOIN AGREEMENT_PARTIES creditorParty ON a.id = creditorParty.agreement_id AND creditorParty.role = 'CREDITOR' JOIN USERS debtorUser ON debtorParty.user_id = debtorUser.id JOIN USERS creditorUser ON creditorParty.user_id = creditorUser.id WHERE t.status = 'PENDING' AND creditorParty.user_id = #{userId} ) x
WHERE x.cursorKey < #{cursorKey}
ORDER BY x.cursorKey DESC LIMIT #{sizePlusOne}
