mapper.update:
UPDATE ITEMS
SET title       = #{title},
    description = #{description},
    file_url    = #{fileUrl},
    file_type   = #{fileType}
WHERE id = #{id}

UPDATE ITEMS
SET file_url  = #{fileUrl},
    file_type = #{fileType}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, type, title, description, status, file_url, file_type, created_at FROM ITEMS WHERE user_id = #{userId}
AND id < #{lastId}
ORDER by id DESC LIMIT #{sizePlusOne}

mapper.update:
UPDATE ITEMS
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT i.id, i.user_id, i.type, i.title, i.description, i.status, i.file_url, i.file_type, IFNULL(agg_summary.loanCount, 0) AS loanCount, agg_summary.lastDebtorName, i.created_at FROM ITEMS i LEFT JOIN (SELECT ra.item_id, COUNT(*) AS loanCount, MAX(CASE WHEN ra.rn = 1 THEN ra.debtorName END) AS lastDebtorName FROM (SELECT a.item_id, u.name AS debtorName, ROW_NUMBER() OVER (PARTITION BY a.item_id ORDER BY a.created_at DESC) AS rn FROM AGREEMENTS a JOIN AGREEMENT_PARTIES ap ON a.id = ap.agreement_id AND ap.role = 'DEBTOR' JOIN USERS u ON ap.user_id = u.id WHERE a.status = 'COMPLETED') ra GROUP BY ra.item_id) agg_summary ON i.id = agg_summary.item_id WHERE i.user_id = #{userId} AND i.type='OBJECT'
AND i.id < #{lastId}
AND i.status = 'AVAILABLE'
AND i.status IN ('ON_LOAN','PENDING_APPROVAL')
ORDER by i.id DESC LIMIT #{sizePlusOne}
