mapper.select:
SELECT id, agreement_id, user_id, type, message, read_at, created_at
FROM NOTIFICATIONS
WHERE user_id = #{userId}
AND read_at IS NULL

  AND id < #{lastId}

ORDER by id DESC
LIMIT #{sizePlusOne}

SELECT id, agreement_id, user_id, type, message, read_at, created_at
FROM NOTIFICATIONS
WHERE id = #{id}

mapper.update:
UPDATE NOTIFICATIONS
SET read_at = #{readAt}
WHERE id = #{id}
