mapper.update:
UPDATE USERS
SET phone = #{phone}
WHERE id = #{id}

UPDATE USERS
SET password = #{password}
WHERE id = #{id}

UPDATE USERS
SET email_verified           = #{emailVerified},
    email_verification_token = #{emailVerificationToken},
    email_token_generated_at = #{emailTokenGeneratedAt}

WHERE id = #{id}

UPDATE USERS
SET provider    = #{provider},
    provider_id = #{providerId}
WHERE id = #{id}

UPDATE USERS
SET password_reset_token = #{passwordResetToken},
password_token_generated_at = #{passwordTokenGeneratedAt}

  ,password = #{password}

WHERE id = #{id}
